sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;

	for(var property in c.data){
		console.log('c.data.' + property + ": " + JSON.stringify(c.data[property]));
	}

	c.selectItem = function(idx) 
	{
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('noteRecord', function(event,data) {
		console.log('2. Listener caught NoteID: ' + $rootScope.noteID); 
		c.server.get({
			action: 'updatedNoteRecord',
			noteID: $rootScope.noteID
		}).then(function(r) {
			//c.data.title = r.data.note.title;
			//c.data.note = r.data.note.note;
			//c.data.noteID = r.data.note.sys_id;
		});
	});

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.notes = [];

	var grNotes = new GlideRecord('x_snc_createnotes_note');
	grNotes.addQuery('user',gs.getUser().getID());
	grNotes.orderByDesc('sys_created_on')
	grNotes.query();
	while(grNotes.next())
	{
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, grNotes, 'number,title,sys_id');		
		noteObj.note = grNotes.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}

	if (input.action == 'updatedNoteRecord') 
	{ 
		gs.addInfoMessage("Yogev");
	}



})();
